/**
 * ============================================================================
 * Snippet: 00-Global-Constants
 * Title: 9o Van 全域常數定義
 * Description: 定義系統所需的全域常數
 * Version: 1.0.0
 * Author: 9o Van Development Team
 * Created: 2025-09-13
 * Modified: 2025-09-13
 * Priority: 1
 * Hook: Run everywhere
 * ============================================================================
 */

// 防止直接訪問
if (!defined('ABSPATH')) {
    exit;
}

// 避免重複定義
if (!defined('NINE_O_VAN_INITIALIZED')) {

    // 標記已初始化
    define('NINE_O_VAN_INITIALIZED', true);

    // 系統識別
    define('NINE_O_VAN_PREFIX', '9o_van_');
    define('NINE_O_VAN_DOMAIN', '9o-van-strip');

    // Google Maps API
    define('NINE_O_VAN_GOOGLE_API_KEY', 'AIzaSyD7m-umnnMHhXTi11_uvI14Rel6mCN1Dz4');

    // 預約狀態
    define('NINE_O_VAN_STATUS_PENDING', 'pending');
    define('NINE_O_VAN_STATUS_CONFIRMED', 'confirmed');
    define('NINE_O_VAN_STATUS_CANCELLED', 'cancelled');
    define('NINE_O_VAN_STATUS_COMPLETED', 'completed');

    // 服務類型
    define('NINE_O_VAN_SERVICE_AIRPORT', 'airport');
    define('NINE_O_VAN_SERVICE_CHARTER', 'charter');

    // 付款狀態
    define('NINE_O_VAN_PAYMENT_PENDING', 'pending');
    define('NINE_O_VAN_PAYMENT_PAID', 'paid');
    define('NINE_O_VAN_PAYMENT_FAILED', 'failed');
    define('NINE_O_VAN_PAYMENT_REFUNDED', 'refunded');

    // 系統限制
    define('NINE_O_VAN_MAX_STOPS', 5);
    define('NINE_O_VAN_MAX_DAYS', 7);
    define('NINE_O_VAN_MAX_STOPS_PER_DAY', 6);

    // Email 設定
    define('NINE_O_VAN_FROM_NAME', '9o Van Strip');
    define('NINE_O_VAN_FROM_EMAIL', 'booking@9ovanstrip.com');

    // 除錯模式
    define('NINE_O_VAN_DEBUG', false);
    define('NINE_O_VAN_LOG_ERRORS', true);
}

// 全域函數：取得資料表名稱
if (!function_exists('_9o_van_table')) {
    function _9o_van_table($name) {
        global $wpdb;
        $tables = array(
            'bookings' => $wpdb->prefix . '9o_van_bookings',
            'payments' => $wpdb->prefix . '9o_van_payments',
            'assignments' => $wpdb->prefix . '9o_van_assignments',
            'logs' => $wpdb->prefix . '9o_van_logs'
        );
        return isset($tables[$name]) ? $tables[$name] : '';
    }
}

// 全域函數：取得管理員信箱
if (!function_exists('_9o_van_admin_email')) {
    function _9o_van_admin_email() {
        return get_option('admin_email');
    }
}
