/**
 * ============================================================================
 * Snippet: 26-Charter-Frontend-Form
 * Title: 包車服務前端表單
 * Description: 生成包車預約的前端表單 HTML 結構，提供短代碼功能
 * Version: 1.0.0
 * Author: 9o Van Development Team
 * Created: 2025-09-14
 * Modified: 2025-09-14
 * Priority: 26
 * Dependencies: 20-Charter-Settings, 25-Charter-Ajax-Handlers
 * Hook: Run everywhere
 * ============================================================================
 */

// 防止直接訪問
if (!defined('ABSPATH')) {
    exit;
}

// 檢查依賴
if (!class_exists('NineOVan_Charter_Settings')) {
    error_log('[26-Charter-Frontend-Form] 錯誤：缺少設定類別');
    return;
}

/**
 * 包車前端表單類別
 */
class NineOVan_Charter_Frontend_Form {

    private static $instance = null;
    private $settings;

    /**
     * 取得單例實例
     */
    public static function get_instance() {
        if (null === self::$instance) {
            self::$instance = new self();
        }
        return self::$instance;
    }

    /**
     * 建構函式
     */
    private function __construct() {
        $this->settings = NineOVan_Charter_Settings::get_instance();
        $this->init_hooks();
    }

    /**
     * 初始化鉤子
     */
    private function init_hooks() {
        // 註冊短代碼（與 legacy 相容）
        if (!shortcode_exists('charter_booking_form')) {
            add_shortcode('charter_booking_form', array($this, 'render_booking_form'));
        }

        // 新增短代碼（更語意化）
        add_shortcode('9o_charter_form', array($this, 'render_booking_form'));

        // 加載 Google Maps API（在頁首）
        add_action('wp_head', array($this, 'load_google_maps'), 5);

        // 註冊表單所需的本地化資料
        add_action('wp_enqueue_scripts', array($this, 'localize_form_data'));
    }

    /**
     * 渲染預約表單
     */
    public function render_booking_form($atts = array()) {
        // 短代碼參數
        $atts = shortcode_atts(array(
            'show_price_panel' => 'yes',
            'max_days' => _9O_VAN_CHARTER_MAX_DAYS,
            'min_booking_days' => _9O_VAN_CHARTER_MIN_BOOKING_DAYS,
            'theme' => 'default' // default, minimal, modern
        ), $atts);

        ob_start();
        ?>
        <div id="charter-booking-app" class="charter-theme-<?php echo esc_attr($atts['theme']); ?>">
            <div class="booking-container">
                <!-- 主表單區域 -->
                <form id="charter-booking-form" class="booking-form">
                    <div class="form-loading">
                        <span>載入中...</span>
                    </div>
                </form>

                <?php if ($atts['show_price_panel'] === 'yes'): ?>
                <!-- 即時報價面板 -->
                <div id="price-panel" class="price-panel">
                    <h3>即時報價</h3>
                    <div class="price-content">
                        <div class="price-loading">請選擇行程...</div>
                    </div>
                </div>
                <?php endif; ?>
            </div>
        </div>

        <!-- 表單配置資料 -->
        <script type="text/javascript">
        window.CharterFormConfig = <?php echo json_encode($this->get_form_config($atts)); ?>;
        </script>
        <?php

        return ob_get_clean();
    }

    /**
     * 取得表單配置
     */
    private function get_form_config($atts) {
        $settings = $this->settings::get_all_settings();

        return array(
            'ajaxUrl' => admin_url('admin-ajax.php'),
            'nonce' => wp_create_nonce('charter_booking'),
            'minDate' => date('Y-m-d', strtotime('+' . $atts['min_booking_days'] . ' days')),
            'maxDays' => $atts['max_days'],
            'maxStops' => _9O_VAN_CHARTER_MAX_STOPS_PER_DAY,
            'googleMapsApiKey' => GOOGLE_MAPS_API_KEY,
            'showPricePanel' => $atts['show_price_panel'] === 'yes',
            'theme' => $atts['theme'],
            'settings' => array(
                'baseRates' => $settings['base_rates'],
                'mountainElevation' => $settings['mountain_elevation'],
                'excludedAreas' => $settings['excluded_areas'],
                'driverSubsidy' => $settings['driver_subsidy'],
                'addonPrices' => $settings['addon_prices']
            ),
            'messages' => $this->get_form_messages(),
            'formSections' => $this->get_form_sections()
        );
    }

    /**
     * 取得表單訊息
     */
    private function get_form_messages() {
        return array(
            'loading' => '載入中...',
            'calculating' => '計算中...',
            'submitting' => '處理中...',
            'success' => '預約成功！請立即支付訂金，我們將在24小時內與您聯繫確認。',
            'error' => '預約失敗，請檢查所有必填欄位',
            'networkError' => '系統錯誤，請稍後再試',
            'invalidDate' => '請至少提前' . _9O_VAN_CHARTER_MIN_BOOKING_DAYS . '天預約，以便安排車輛與司機',
            'maxDaysExceeded' => '行程天數不能超過' . _9O_VAN_CHARTER_MAX_DAYS . '天',
            'maxStopsExceeded' => '每日最多只能新增' . _9O_VAN_CHARTER_MAX_STOPS_PER_DAY . '個停靠點',
            'excludedArea' => '抱歉，我們不提供{area}地區的服務',
            'mountainAlert' => '系統偵測到可能包含山區路線，將自動加收山區服務費 NT$1,000/日。',
            'noEmailNotice' => '※ 未填寫將不會收到確認信件通知'
        );
    }

    /**
     * 取得表單區塊定義
     */
    private function get_form_sections() {
        return array(
            'header' => array(
                'title' => '🚐 包車旅遊預約',
                'subtitle' => '專業司機·舒適九人座·全台走透透'
            ),
            'basic' => array(
                'title' => '📅 基本資訊',
                'fields' => array(
                    'trip_days' => array(
                        'label' => '使用天數',
                        'type' => 'select',
                        'required' => true,
                        'options' => $this->get_trip_days_options()
                    ),
                    'start_date' => array(
                        'label' => '出發日期',
                        'type' => 'date',
                        'required' => true,
                        'min' => date('Y-m-d', strtotime('+' . _9O_VAN_CHARTER_MIN_BOOKING_DAYS . ' days'))
                    ),
                    'start_time' => array(
                        'label' => '出發時間',
                        'type' => 'time',
                        'required' => true,
                        'default' => '08:00'
                    ),
                    'passengers' => array(
                        'label' => '乘客人數',
                        'type' => 'number',
                        'required' => true,
                        'min' => 1,
                        'max' => 8,
                        'default' => 4
                    )
                )
            ),
            'routes' => array(
                'title' => '🗺️ 每日行程規劃',
                'dynamic' => true,
                'template' => 'daily_route'
            ),
            'driver' => array(
                'title' => '🏨 司機安排',
                'fields' => array(
                    'driver_accommodation' => array(
                        'label' => '司機住宿',
                        'type' => 'radio',
                        'options' => array(
                            'self' => array(
                                'label' => '提供住宿',
                                'price' => '由客戶安排'
                            ),
                            'book' => array(
                                'label' => '代訂住宿',
                                'price' => '+2,000/晚'
                            )
                        ),
                        'default' => 'self',
                        'condition' => 'trip_days > 1'
                    ),
                    'driver_meals' => array(
                        'label' => '司機用餐',
                        'type' => 'radio',
                        'options' => array(
                            'provided' => array(
                                'label' => '提供餐點',
                                'price' => '由客戶安排'
                            ),
                            'allowance' => array(
                                'label' => '餐費補貼',
                                'price' => '+400/日'
                            )
                        ),
                        'default' => 'provided'
                    )
                )
            ),
            'addon' => array(
                'title' => '🔧 加購項目',
                'notice' => '根據《小型車附載幼童安全乘坐實施及宣導辦法》，<strong>4歲以下孩童皆需使用安全座椅</strong>',
                'fields' => array(
                    'child_seats' => array(
                        'label' => '嬰兒安全座椅 (每張NT$100)',
                        'type' => 'number',
                        'min' => 0,
                        'max' => 4,
                        'default' => 0
                    ),
                    'booster_seats' => array(
                        'label' => '兒童增高墊 (每張NT$100)',
                        'type' => 'number',
                        'min' => 0,
                        'max' => 4,
                        'default' => 0
                    )
                )
            ),
            'contact' => array(
                'title' => '📞 聯絡資訊',
                'fields' => array(
                    'customer_name' => array(
                        'label' => '聯絡人姓名',
                        'type' => 'text',
                        'required' => true,
                        'placeholder' => '請輸入您的姓名'
                    ),
                    'customer_phone' => array(
                        'label' => '電話 (含國際區號)',
                        'type' => 'tel',
                        'required' => true,
                        'placeholder' => '+886912345678 或 0912345678',
                        'pattern' => '(\\+[0-9]{1,4})?[0-9]{9,15}',
                        'title' => '請輸入有效電話號碼，可含國際區號'
                    ),
                    'customer_email' => array(
                        'label' => 'Email（選填）',
                        'type' => 'email',
                        'required' => false,
                        'placeholder' => 'example@email.com',
                        'notice' => '※ 未填寫將不會收到確認信件通知'
                    ),
                    'notes' => array(
                        'label' => '備註說明',
                        'type' => 'textarea',
                        'required' => false,
                        'rows' => 3
                    )
                )
            )
        );
    }

    /**
     * 取得行程天數選項
     */
    private function get_trip_days_options() {
        $options = array();
        for ($i = 1; $i <= _9O_VAN_CHARTER_MAX_DAYS; $i++) {
            $options[$i] = $i . '天';
        }
        return $options;
    }

    /**
     * 載入 Google Maps API
     */
    public function load_google_maps() {
        global $post;

        // 檢查是否在需要的頁面
        if (!$this->should_load_maps()) {
            return;
        }

        $api_key = GOOGLE_MAPS_API_KEY;
        ?>
        <!-- Google Maps API for Charter Booking -->
        <script src="https://maps.googleapis.com/maps/api/js?key=<?php echo esc_attr($api_key); ?>&libraries=places&language=zh-TW"></script>
        <?php
    }

    /**
     * 檢查是否應該載入地圖
     */
    private function should_load_maps() {
        global $post;

        // 檢查頁面標題
        if (is_page('包車旅遊預約') || is_page('charter-booking')) {
            return true;
        }

        // 檢查短代碼
        if ($post && has_shortcode($post->post_content, 'charter_booking_form')) {
            return true;
        }

        if ($post && has_shortcode($post->post_content, '9o_charter_form')) {
            return true;
        }

        return false;
    }

    /**
     * 本地化表單資料
     */
    public function localize_form_data() {
        if (!$this->should_load_maps()) {
            return;
        }

        // 註冊本地化資料給 JavaScript 使用
        wp_localize_script('charter-frontend-js', 'CharterFormData', array(
            'ajax_url' => admin_url('admin-ajax.php'),
            'nonce' => wp_create_nonce('charter_booking'),
            'min_date' => date('Y-m-d', strtotime('+' . _9O_VAN_CHARTER_MIN_BOOKING_DAYS . ' days')),
            'max_days' => _9O_VAN_CHARTER_MAX_DAYS,
            'max_stops' => _9O_VAN_CHARTER_MAX_STOPS_PER_DAY,
            'messages' => $this->get_form_messages()
        ));
    }

    /**
     * 生成每日路線模板（供 JavaScript 使用）
     */
    public static function get_daily_route_template() {
        ob_start();
        ?>
        <script type="text/template" id="daily-route-template">
            <div class="day-route-container" data-day="{{dayIndex}}">
                <div class="day-header">
                    <div class="day-title">
                        <span>Day {{dayNum}}</span>
                        {{#isFirstDay}}<span class="day-badge">出發日</span>{{/isFirstDay}}
                        {{#isLastDay}}<span class="day-badge">返程日</span>{{/isLastDay}}
                    </div>
                    <div class="day-date" id="day-date-{{dayIndex}}">{{dayDate}}</div>
                </div>

                <div class="form-row">
                    <div class="form-group">
                        <label>起點地址 <span class="required">*</span></label>
                        <div class="address-input-wrapper">
                            <input type="text" name="origin_{{dayIndex}}" id="origin_{{dayIndex}}"
                                   class="address-autocomplete" placeholder="輸入地址或地標"
                                   value="{{origin}}" required>
                            <div class="address-suggestions" id="origin_{{dayIndex}}_suggestions"></div>
                        </div>
                    </div>

                    <div class="form-group">
                        <label>終點地址 <span class="required">*</span></label>
                        <div class="address-input-wrapper">
                            <input type="text" name="destination_{{dayIndex}}" id="destination_{{dayIndex}}"
                                   class="address-autocomplete" placeholder="輸入地址或地標"
                                   value="{{destination}}" required>
                            <div class="address-suggestions" id="destination_{{dayIndex}}_suggestions"></div>
                        </div>
                    </div>
                </div>

                <div class="form-group">
                    <label>預排停靠點（選填，最多{{maxStops}}個）</label>
                    <div class="stops-container" id="stops-container-{{dayIndex}}"></div>
                    <button type="button" class="btn-add-stop" data-day="{{dayIndex}}">
                        + 新增停靠點
                    </button>
                </div>

                <div class="mountain-alert alert-box" id="mountain-alert-{{dayIndex}}">
                    <strong>⛰️ 山區路線提醒：</strong>
                    系統偵測到可能包含山區路線，將自動加收山區服務費 NT$1,000/日。
                </div>

                <div class="excluded-alert alert-box" id="excluded-alert-{{dayIndex}}">
                    <strong>❌ 服務範圍提醒：</strong>
                    很抱歉，我們不提供司馬庫斯地區的服務。
                </div>
            </div>
        </script>
        <?php
        return ob_get_clean();
    }

    /**
     * 生成停靠點模板
     */
    public static function get_stop_template() {
        ob_start();
        ?>
        <script type="text/template" id="stop-template">
            <div class="stop-item" data-index="{{stopIndex}}" data-day="{{dayIndex}}">
                <div class="address-input-wrapper" style="flex: 1;">
                    <input type="text" id="stop_{{dayIndex}}_{{stopIndex}}" 
                           class="address-autocomplete stop-input"
                           placeholder="停靠點 {{stopNum}}" value="{{address}}">
                    <div class="address-suggestions" id="stop_{{dayIndex}}_{{stopIndex}}_suggestions"></div>
                </div>
                <div class="stop-controls">
                    <button type="button" class="stop-btn" data-action="up" data-day="{{dayIndex}}" 
                            {{#isFirst}}disabled{{/isFirst}}>↑</button>
                    <button type="button" class="stop-btn" data-action="down" data-day="{{dayIndex}}" 
                            {{#isLast}}disabled{{/isLast}}>↓</button>
                    <button type="button" class="stop-btn delete" data-action="delete" 
                            data-day="{{dayIndex}}">×</button>
                </div>
            </div>
        </script>
        <?php
        return ob_get_clean();
    }
}

// 初始化
NineOVan_Charter_Frontend_Form::get_instance();

/**
 * 全域輔助函式
 */

// 渲染包車表單
if (!function_exists('_9o_van_charter_form')) {
    function _9o_van_charter_form($atts = array()) {
        return NineOVan_Charter_Frontend_Form::get_instance()->render_booking_form($atts);
    }
}

// 檢查是否為包車頁面
if (!function_exists('_9o_van_is_charter_page')) {
    function _9o_van_is_charter_page() {
        global $post;

        if (is_page('包車旅遊預約') || is_page('charter-booking')) {
            return true;
        }

        if ($post && (has_shortcode($post->post_content, 'charter_booking_form') || 
                     has_shortcode($post->post_content, '9o_charter_form'))) {
            return true;
        }

        return false;
    }
}

// 取得表單模板
if (!function_exists('_9o_van_get_charter_templates')) {
    function _9o_van_get_charter_templates() {
        $templates = '';
        $templates .= NineOVan_Charter_Frontend_Form::get_daily_route_template();
        $templates .= NineOVan_Charter_Frontend_Form::get_stop_template();
        return $templates;
    }
}

// 記錄模組載入
if (defined('_9O_VAN_DEBUG') && _9O_VAN_DEBUG) {
    error_log('[26-Charter-Frontend-Form] 模組已載入 - v1.0.0');
}
